import React, {useState, useMemo, useEffect} from 'react'

import Result from "./Result";



const synth = window.speechSynthesis;


function App() {

  const voices = useMemo(() => synth.getVoices(), [])

  const [voiceSelected, setVoiceSelected] = useState("Google US English")

  const [text, setText] = useState("")

  const [isSpeaking, setIsSpeaking] = useState("")

  const dictionaryApi = (text) => {
    let url = `https://api.dictionaryapi.dev/api/v2/entries/en/${text}`;
    fetch(url)
      .then(res => res.json())
      .then(result => {
        console.log(result)
      })
  }

  useEffect(() => {
    if(!text.trim()) return;

    dictionaryApi(text)
  }, [text])

  const startSpeech = (text) => {
    const utterance = new SpeechSynthesisUtterance(text)

    const voice = voices.find(voice => voice.name === voiceSelected)

    utterance.voice = voice
    synth.speak(utterance)
  }

  const handleSpeech = () => {
    if(!text.trim()) return;
    if(!synth.speaking){
      startSpeech(text)
      setIsSpeaking("speak")
    }
    else {
      synth.cancel()
    }

    setInterval(() => {
      if(!synth.speaking){
        setIsSpeaking("")
      }
    }, 100)
  }



  return (
    <div className="container">
      <h1>English Dictionary</h1>

      <form>
        <div className="row">
          <textarea cols="30" rows="4"
          placeholder="Enter the text"
          value={text}
          onChange = { e => setText(e.target.value)}     
          />

          <div className="voices-icons">
            <div className="select-voices">
              <select value={voiceSelected}
              onChange = {e => setVoiceSelected(e.target.value)}
              >

                {
                  voices.map(voices => (
                    <option key={voices.name} value={voices.name}>{voices.name}</option>
                  ))
                }


              </select>
            </div>

            <i className={`fa-solid fa-volume-high ${isSpeaking}`}
            onClick = {handleSpeech}
            />
          </div>
        </div>
      </form>

      <Result/>
    </div>
  );
}

export default App;
